<template>
  <div id="app" :class="{ 'app--printmode': printMode }">
    <router-view />
    <canvas ref="app__canvas-confetti" class="app__canvas-confetti" />
  </div>
</template>

<script>
import { Bus } from "./main";
const confetti = require("canvas-confetti");

export default {
  name: "App",
  data() {
    return {
      lang: "en",
      printMode: false,
    };
  },
  mounted() {
    this.myConfetti = confetti.create(this.$refs["app__canvas-confetti"], {
      resize: true,
      useWorker: true,
    });

    const urlSearchParams = new URLSearchParams(window.location.search);
    const params = Object.fromEntries(urlSearchParams.entries());
    this.printMode = params.printmode;

    Bus.$on("download-cv", () => {
      let counter = 0;
      const settings = {
        particleCount: 75,
        spread: 90,
        colors: ["#B93E36", "#f4f5f0", "#1a201c"],
        startVelocity: 75,
      };

      const confetti = this.myConfetti;

      (function frame() {
        confetti({ ...settings, angle: 45, origin: { x: 0, y: 1 } });
        confetti({ ...settings, angle: 135, origin: { x: 1, y: 1 } });
        counter++;
        counter < 10 && setTimeout(frame, 60);
      })();
    });
  },
};
</script>

