<template>
  <div class="wrapper">
    <div class="noise" :class="{ 'noise--printmode': printMode }" />
    <SideBar
      :links="currentCv.links"
      :cv="currentCv.cv"
      :lang="lang"
      :printMode="printMode"
    />
    <div class="resume" :class="{ 'resume--printmode': printMode }">
      <div class="container">
        <Heading :heading="currentCv.heading" />
        <div class="grid">
          <div class="column--1">
            <Skills :skills="currentCv.skills" />
            <Qualities :qualities="currentCv.qualities" />
            <Languages :languages="currentCv.languages" />
          </div>
          <div class="column--2">
            <Experience :jobs="currentCv.jobs" />
            <Education :courses="currentCv.studies" />
            <Education :courses="currentCv.courses" class="courses" />
          </div>
        </div>
        <Contact :contact="currentCv.contact" />
      </div>
      <p class="version">
        v{{ cv.options.version }} - {{ lang.toUpperCase() }}
      </p>
    </div>
  </div>
</template>

<script>
import SideBar from "./SideBar.vue";

import Heading from "./Heading";
import Skills from "./Skills";
import Qualities from "./Qualities";
import Experience from "./Experience";
import Education from "./Education";
import Languages from "./Languages.vue";
import Contact from "./Contact.vue";

import CV from "../data";

export default {
  name: "Resume",
  components: {
    SideBar,
    Heading,
    Skills,
    Qualities,
    Experience,
    Education,
    Languages,
    Contact,
  },
  metaInfo() {
    return this.currentCv.meta;
  },
  data() {
    return {
      cv: CV,
      lang: "en",
      printMode: false,
    };
  },
  mounted() {
    const urlSearchParams = new URLSearchParams(window.location.search);
    const params = Object.fromEntries(urlSearchParams.entries());
    this.printMode = params.printmode;
  },
  created() {
    this.lang = this.$route.params.locale;
    document.documentElement.lang = this.lang;
  },
  watch: {
    $route() {
      this.lang = this.$route.params.locale;
      document.documentElement.lang = this.lang;
    },
  },
  computed: {
    currentCv() {
      return this.cv.languages[this.lang];
    },
  },
};
</script>

